  PRIMARY KEY  (`CUST_ID`),
  KEY `IDX_NAME` (`CUST_CFNAME`,`CUST_CSNAME`,`CUST_EFNAME`,`CUST_ESNAME`),
  KEY `IDX_INDUSTRY` (`CUST_INDUSTRY1`,`CUST_INDUSTRY2`),
  KEY `IDX_PINYIN` (`PINYIN`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

