$(function() {
	/*
	 获取元素外宽度（边框+内边距+宽度）
	 * */
	function f(l) {
		var k = 0;
		$(l).each(function() {
			k += $(this).outerWidth(true)//outerWidth()函数用于获取当前匹配元素的外宽度。
		});
		return k
	}
	/*
	 * 用于计算页签滚动的距离
	 o--tab指定页签前的宽度
	 q--tab指定页签后的宽度
	 l--一行中不包含tab页签的宽度，即左右箭头+关闭操作+退出按钮的宽度
	 k--一行中去掉固定按钮的宽度
	 * */
	function g(n) {
		var o = f($(n).prevAll()),
			q = f($(n).nextAll());
		var l = f($(".content-tabs").children().not(".J_menuTabs"));
		var k = $(".content-tabs").outerWidth(true) - l;
		var p = 0;
		if($(".page-tabs-content").outerWidth() < k) {
			p = 0
		} else {
			if(q <= (k - $(n).outerWidth(true) - $(n).next().outerWidth(true))) {
				if((k - $(n).next().outerWidth(true)) > q) {
					p = o;
					var m = n;
					while((p - $(m).outerWidth()) > ($(".page-tabs-content").outerWidth() - k)) {
						p -= $(m).prev().outerWidth();
						m = $(m).prev()
					}
				}
			} else {
				if(o > (k - $(n).outerWidth(true) - $(n).prev().outerWidth(true))) {
					p = o - $(n).prev().outerWidth(true)
				}
			}
		}
		$(".page-tabs-content").animate({
			marginLeft: 0 - p + "px"
		}, "fast")
	}

	function a() {
		var o = Math.abs(parseInt($(".page-tabs-content").css("margin-left")));
		var l = f($(".content-tabs").children().not(".J_menuTabs"));
		var k = $(".content-tabs").outerWidth(true) - l;
		var p = 0;
		if($(".page-tabs-content").width() < k) {
			return false
		} else {
			var m = $(".J_menuTab:first");
			var n = 0;
			while((n + $(m).outerWidth(true)) <= o) {
				n += $(m).outerWidth(true);
				m = $(m).next()
			}
			n = 0;
			if(f($(m).prevAll()) > k) {
				while((n + $(m).outerWidth(true)) < (k) && m.length > 0) {
					n += $(m).outerWidth(true);
					m = $(m).prev()
				}
				p = f($(m).prevAll())
			}
		}
		$(".page-tabs-content").animate({
			marginLeft: 0 - p + "px"
		}, "fast")
	}

	function b() {
		var o = Math.abs(parseInt($(".page-tabs-content").css("margin-left")));
		var l = f($(".content-tabs").children().not(".J_menuTabs"));
		var k = $(".content-tabs").outerWidth(true) - l;
		var p = 0;
		if($(".page-tabs-content").width() < k) {
			return false
		} else {
			var m = $(".J_menuTab:first");
			var n = 0;
			while((n + $(m).outerWidth(true)) <= o) {
				n += $(m).outerWidth(true);
				m = $(m).next()
			}
			n = 0;
			while((n + $(m).outerWidth(true)) < (k) && m.length > 0) {
				n += $(m).outerWidth(true);
				m = $(m).next()
			}
			p = f($(m).prevAll());
			if(p > 0) {
				$(".page-tabs-content").animate({
					marginLeft: 0 - p + "px"
				}, "fast")
			}
		}
	}
	$(".J_menuItem").each(function(k) {
		if(!$(this).attr("data-index")) {
			$(this).attr("data-index", k)
		}
	});
	/*
	 动态生成tab页签和iframe
	 isPage判断是本页面还是iframe页面内
	 * */
	function c(e) {
		e.preventDefault();
		if(!$(this).parent().hasClass('active')) {
			$(".sBox ul li.active").removeClass('active');
			$(this).parent().siblings('.active').removeClass('active');
			$(this).parent().addClass('active');
		}
		var o = $(this).attr("href"),
			m = $(this).data("index"),
			l = $.trim($(this).text()),
			title=$(this).data("title"),
			isPage=$(this).data("ispage");
			k = true;
		if(o == undefined || $.trim(o).length == 0) {
			return false
		}
		var navTabs=$(".J_menuTab");
		if(isPage){
			navTabs=$(".J_menuTab",window.parent.document);
		}
		navTabs.each(function() {
			if($(this).data("id") == o) {
				if(!$(this).hasClass("active")) {
					$(this).addClass("active").siblings(".J_menuTab").removeClass("active");
					g(this);
					if(isPage){
						$(".J_mainContent .J_iframe",window.parent.document).each(function() {
							if($(this).data("id") == o) {
								$(this).show().siblings(".J_iframe").hide();
								return false
							}
						})
					}else{
						$(".J_mainContent .J_iframe").each(function() {
							if($(this).data("id") == o) {
								$(this).show().siblings(".J_iframe").hide();
								return false
							}
						})
					}
				}
				k = false;
				return false
			}
		});
		if(k) {
			if(title){
				var p = '<a href="javascript:;" class="active J_menuTab" data-id="' + o + '">' + title + ' <i class="fa fa-times-circle"></i></a>';
			}else{
				var p = '<a href="javascript:;" class="active J_menuTab" data-id="' + o + '">' + l + ' <i class="fa fa-times-circle"></i></a>';
			}
			var n = '<iframe class="J_iframe" id="iframe' + o + '" name="iframe' + m + '" width="100%" height="100%" src="' + o + '" frameborder="0" data-id="' + o + '" seamless></iframe>';
			if(isPage){//如果点击的是iframe页面内的元素
				$('#content-main>.loading-div', window.parent.document).css({ display: 'block' });
				$(".J_menuTab",window.parent.document).removeClass("active");
				$('.J_mainContent', window.parent.document).find("iframe.J_iframe").hide().parents(".J_mainContent").append(n);
				loadingPage(isPage,o);
				$(".J_menuTabs .page-tabs-content",window.parent.document).append(p);
			}else{//如果点击的是同一页面的元素
				$('#content-main>.loading-div').css({ display: 'block' });
				$(".J_menuTab").removeClass("active");
				$(".J_mainContent").find("iframe.J_iframe").hide().parents(".J_mainContent").append(n);
				/*页面前加载*/
				loadingPage(isPage,o);
				$(".J_menuTabs .page-tabs-content").append(p);
			}
			g($(".J_menuTab.active"))
		}
		return false
	}
	/*页面加载动画效果*/
	function loadingPage(isPage,targetUrl){
		if(isPage){
			$('#content-main>.loading-div', window.parent.document).css({ display: 'block' });
			$('.J_iframe[data-id="' + targetUrl + '"]', window.parent.document).bind('load', function() {
                $('#content-main>.loading-div', window.parent.document).css({ display: 'none' });
            });
		}else{
			$('#content-main>.loading-div').css({ display: 'block' });
			$('.J_iframe[data-id="' + targetUrl + '"]').bind('load', function() {
	            $('#content-main>.loading-div').css({ display: 'none' });
	        });
		}
		
	}
	/*J_menuItem类点击，添加/打开对应页签*/
	$(".J_menuItem").on("click", c);
	function h() {
		var m = $(this).parents(".J_menuTab").data("id");
		var l = $(this).parents(".J_menuTab").width();
		if($(this).parents(".J_menuTab").hasClass("active")) {
			if($(this).parents(".J_menuTab").next(".J_menuTab").size()) {
				var k = $(this).parents(".J_menuTab").next(".J_menuTab:eq(0)").data("id");
				$(this).parents(".J_menuTab").next(".J_menuTab:eq(0)").addClass("active");
				$(".J_mainContent .J_iframe").each(function() {
					if($(this).data("id") == k) {
						$(this).show().siblings(".J_iframe").hide();
						return false
					}
				});
				var n = parseInt($(".page-tabs-content").css("margin-left"));
				if(n < 0) {
					$(".page-tabs-content").animate({
						marginLeft: (n + l) + "px"
					}, "fast")
				}
				$(this).parents(".J_menuTab").remove();
				$(".J_mainContent .J_iframe").each(function() {
					if($(this).data("id") == m) {
						$(this).remove();
						return false
					}
				})
			}
			if($(this).parents(".J_menuTab").prev(".J_menuTab").size()) {
				var k = $(this).parents(".J_menuTab").prev(".J_menuTab:last").data("id");
				$(this).parents(".J_menuTab").prev(".J_menuTab:last").addClass("active");
				$(".J_mainContent .J_iframe").each(function() {
					if($(this).data("id") == k) {
						$(this).show().siblings(".J_iframe").hide();
						return false
					}
				});
				$(this).parents(".J_menuTab").remove();
				$(".J_mainContent .J_iframe").each(function() {
					if($(this).data("id") == m) {
						$(this).remove();
						return false
					}
				})
			}
		} else {
			$(this).parents(".J_menuTab").remove();
			$(".J_mainContent .J_iframe").each(function() {
				if($(this).data("id") == m) {
					$(this).remove();
					return false
				}
			});
			g($(".J_menuTab.active"))
		}
		return false
	}
	$(".J_menuTabs").on("click", ".J_menuTab i", h);
	/*关闭其它页签*/
	function i() {
		$(".page-tabs-content").children("[data-id]").not(":first").not(".active").each(function() {
			$('.J_iframe[data-id="' + $(this).data("id") + '"]').remove();
			$(this).remove()
		});
		$(".page-tabs-content").css("margin-left", "0")
	}
	$(".J_tabCloseOther").on("click", i);
	/*刷新当前页签*/
	function j() {
		var isPage=$(this).data("ispage");
		if(isPage){
			var targerUrl=$(".J_menuTab.active",window.parent.document).data("id");
			var targetIframe=$('iframe[src="'+targerUrl+'"]',window.parent.document);
		}else{
			var targerUrl=$(".J_menuTab.active").data("id");
			var targetIframe=$('iframe[src="'+targerUrl+'"]');
		}
		
		targetIframe.attr('src', targerUrl);
		loadingPage(isPage,targerUrl);
	}
	$(".J_tabShowActive").on("click", j);

	function e() {
		/*点击 刷新当前页面*/
		var targerUrl=$(this).data("id");
		var targetIframe=$('iframe[src="'+targerUrl+'"]')
		targetIframe.attr('src', targerUrl);
		var isPage=$(this).data("ispage");
		loadingPage(isPage,targerUrl);
		if(!$(this).hasClass("active")) {
			var k = $(this).data("id");
			$(".J_mainContent .J_iframe").each(function() {
				if($(this).data("id") == k) {
					$(this).show().siblings(".J_iframe").hide();
					return false
				}
			});
			$(this).addClass("active").siblings(".J_menuTab").removeClass("active");
			g(this)
		}
	}
	$(".J_menuTabs").on("click", ".J_menuTab", e);

	function d() {
		var l = $('.J_iframe[data-id="' + $(this).data("id") + '"]');
		var k = l.attr("src")
	}
	$(".J_menuTabs").on("dblclick", ".J_menuTab", d);
	$(".J_tabLeft").on("click", a);
	$(".J_tabRight").on("click", b);
	/*关闭所有*/
	$(".J_tabCloseAll").on("click", function() {
		$(".page-tabs-content").children("[data-id]").not(":first").each(function() {
			$('.J_iframe[data-id="' + $(this).data("id") + '"]').remove();
			$(this).remove()
		});
		$(".page-tabs-content").children("[data-id]:first").each(function() {
			$('.J_iframe[data-id="' + $(this).data("id") + '"]').show();
			$(this).addClass("active")
		});
		$(".page-tabs-content").css("margin-left", "0")
	})
});